pragma solidity >=0.8.0 <0.9.0;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/interfaces/IERC2981.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./MusicRevenueVault.sol";

/**
 * @title Music721
 * @dev 简化的音乐NFT合约，单一音乐作品的多份NFT
 */
contract Music721 is
    ERC721,
    ERC721Enumerable,
    IERC2981,
    Ownable,
    ReentrancyGuard
{
    using SafeERC20 for IERC20;

    // 音乐作品信息
    struct MusicInfo {
        string title; // 歌曲标题
        string artist; // 艺术家
        string uriCID; // IPFS/Arweave CID
        uint256 maxSupply; // 最大供应量
        uint256 price; // 单价
        address currency; // ETH or ERC20
        uint96 royaltyBps; // ERC-2981 二级市场分成 (基点)
        address vault; // RevenueVault 地址
        bool mintActive; // 是否可铸造
    }

    // 音乐作品信息
    MusicInfo public musicInfo;

    // 下一个可用的 tokenId
    uint256 public nextTokenId = 1;

    // 基础URI
    string private _baseTokenURI;

    event MusicInfoSet(
        string title,
        string artist,
        uint256 maxSupply,
        uint256 price,
        address currency
    );

    event TokenMinted(
        uint256 indexed tokenId,
        address indexed to,
        uint256 quantity
    );

    constructor(
        string memory name,
        string memory symbol,
        string memory baseURI
    ) ERC721(name, symbol) Ownable(msg.sender) {
        _baseTokenURI = baseURI;
    }

    /**
     * @dev 设置音乐作品信息（仅限owner，只能设置一次）
     */
    function setMusicInfo(
        string memory title,
        string memory artist,
        string memory uriCID,
        uint256 maxSupply,
        uint256 price,
        address currency,
        uint96 royaltyBps,
        address vault
    ) external onlyOwner {
        require(bytes(musicInfo.title).length == 0, "Music info already set");
        require(maxSupply > 0, "Max supply must be > 0");
        require(royaltyBps <= 1000, "Royalty too high"); // 最大10%
        require(vault != address(0), "Invalid vault address");

        musicInfo = MusicInfo({
            title: title,
            artist: artist,
            uriCID: uriCID,
            maxSupply: maxSupply,
            price: price,
            currency: currency,
            royaltyBps: royaltyBps,
            vault: vault,
            mintActive: true
        });

        emit MusicInfoSet(title, artist, maxSupply, price, currency);
    }

    /**
     * @dev 铸造NFT
     */
    function mint(uint256 quantity) external payable nonReentrant {
        require(musicInfo.mintActive, "Minting not active");
        require(quantity > 0, "Quantity must be > 0");
        require(
            nextTokenId + quantity - 1 <= musicInfo.maxSupply,
            "Exceeds max supply"
        );

        uint256 totalCost = musicInfo.price * quantity;

        // 处理支付
        if (musicInfo.currency == address(0)) {
            require(msg.value >= totalCost, "Insufficient ETH");
            if (msg.value > totalCost) {
                payable(msg.sender).transfer(msg.value - totalCost);
            }
        } else {
            IERC20(musicInfo.currency).safeTransferFrom(
                msg.sender,
                address(this),
                totalCost
            );
        }

        // 铸造NFT
        uint256 startTokenId = nextTokenId;
        for (uint256 i = 0; i < quantity; i++) {
            uint256 tokenId = nextTokenId++;
            _safeMint(msg.sender, tokenId);
        }

        // 将收益发送到金库
        _sendRevenueToVault(musicInfo.currency, totalCost, "mint");

        emit TokenMinted(startTokenId, msg.sender, quantity);
    }

    /**
     * @dev 批量铸造（仅限owner）
     */
    function mintBatch(
        address[] memory recipients,
        uint256[] memory quantities
    ) external onlyOwner {
        require(
            recipients.length == quantities.length,
            "Arrays length mismatch"
        );
        require(musicInfo.mintActive, "Minting not active");

        uint256 totalQuantity = 0;
        for (uint256 i = 0; i < quantities.length; i++) {
            totalQuantity += quantities[i];
        }
        require(
            nextTokenId + totalQuantity - 1 <= musicInfo.maxSupply,
            "Exceeds max supply"
        );

        for (uint256 i = 0; i < recipients.length; i++) {
            address recipient = recipients[i];
            uint256 quantity = quantities[i];

            for (uint256 j = 0; j < quantity; j++) {
                uint256 tokenId = nextTokenId++;
                _safeMint(recipient, tokenId);
            }
        }
    }

    /**
     * @dev 设置铸造状态
     */
    function setMintActive(bool active) external onlyOwner {
        require(bytes(musicInfo.title).length > 0, "Music info not set");
        musicInfo.mintActive = active;
    }

    /**
     * @dev 更新价格
     */
    function updatePrice(uint256 newPrice) external onlyOwner {
        require(bytes(musicInfo.title).length > 0, "Music info not set");
        musicInfo.price = newPrice;
    }

    /**
     * @dev 获取已铸造数量
     */
    function getMintedSupply() external view returns (uint256) {
        return nextTokenId - 1;
    }

    /**
     * @dev 获取剩余可铸造数量
     */
    function getRemainingSupply() external view returns (uint256) {
        return musicInfo.maxSupply - (nextTokenId - 1);
    }

    /**
     * @dev 重写_increaseBalance以支持ERC721Enumerable
     */
    function _increaseBalance(
        address account,
        uint128 value
    ) internal override(ERC721, ERC721Enumerable) {
        super._increaseBalance(account, value);
    }

    /**
     * @dev 重写_update以支持ERC721Enumerable
     */
    function _update(
        address to,
        uint256 tokenId,
        address auth
    ) internal override(ERC721, ERC721Enumerable) returns (address) {
        return super._update(to, tokenId, auth);
    }

    /**
     * @dev ERC-2981 版税信息
     */
    function royaltyInfo(
        uint256 tokenId,
        uint256 salePrice
    ) external view override returns (address, uint256) {
        require(_ownerOf(tokenId) != address(0), "Token not exists");

        uint256 royaltyAmount = (salePrice * musicInfo.royaltyBps) / 10000;
        return (musicInfo.vault, royaltyAmount);
    }

    /**
     * @dev 支持的接口
     */
    function supportsInterface(
        bytes4 interfaceId
    ) public view override(ERC721, ERC721Enumerable, IERC165) returns (bool) {
        return
            interfaceId == type(IERC2981).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    /**
     * @dev 获取token URI
     */
    function tokenURI(
        uint256 tokenId
    ) public view override returns (string memory) {
        require(_ownerOf(tokenId) != address(0), "Token not exists");

        return string(abi.encodePacked(_baseTokenURI, musicInfo.uriCID));
    }

    /**
     * @dev 设置基础URI
     */
    function setBaseURI(string memory baseURI) external onlyOwner {
        _baseTokenURI = baseURI;
    }

    /**
     * @dev 内部函数：发送收益到金库
     */
    function _sendRevenueToVault(
        address currency,
        uint256 amount,
        string memory source
    ) internal {
        if (currency == address(0)) {
            // ETH
            MusicRevenueVault(payable(musicInfo.vault)).receiveRevenue{
                value: amount
            }(
                1,
                currency,
                amount,
                source // 使用固定的editionId = 1
            );
        } else {
            // ERC20
            IERC20(currency).forceApprove(musicInfo.vault, amount);
            MusicRevenueVault(payable(musicInfo.vault)).receiveRevenue(
                1,
                currency,
                amount,
                source
            );
        }
    }

    /**
     * @dev 紧急提取函数（仅限owner）
     */
    function emergencyWithdraw(
        address token,
        uint256 amount
    ) external onlyOwner {
        if (token == address(0)) {
            payable(owner()).transfer(amount);
        } else {
            IERC20(token).safeTransfer(owner(), amount);
        }
    }

    // 接收ETH
    receive() external payable {}
}
","keccak256":"0xbf9623fc4370dbf2d72ac777a6362d268e4aec3fc39c78d500a74d5052837b97","license":"MIT"},"contracts/MusicNFTFactory.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "./Music721.sol";
import "./MusicRevenueVault.sol";

/**
 * @title MusicNFTFactory
 * @dev 音乐NFT系统工厂合约，用于一键部署完整系统
 */
contract MusicNFTFactory {
    event MusicNFTSystemDeployed(
        address indexed creator,
        address nftContract,
        address vault,
        string name,
        string symbol
    );

    struct DeployedSystem {
        address nftContract;
        address vault;
        uint256 timestamp;
    }

    mapping(address => DeployedSystem[]) public deployedSystems;

    /**
     * @dev 部署完整的音乐NFT系统
     */
    function deployMusicNFTSystem(
        string memory name,
        string memory symbol,
        string memory baseURI
    ) external returns (address nftContract, address vault) {
        // 1. 部署收益金库
        vault = address(new MusicRevenueVault(address(0))); // 先传入零地址，稍后更新

        // 2. 部署NFT合约
        nftContract = address(new Music721(name, symbol, baseURI));

        // 3. 更新金库的NFT合约地址
        MusicRevenueVault(payable(vault)).transferOwnership(msg.sender);

        // 4. 转移NFT合约所有权
        Music721(payable(nftContract)).transferOwnership(msg.sender);

        // 5. 记录部署信息
        deployedSystems[msg.sender].push(DeployedSystem({
            nftContract: nftContract,
            vault: vault,
            timestamp: block.timestamp
        }));

        emit MusicNFTSystemDeployed(msg.sender, nftContract, vault, name, symbol);

        return (nftContract, vault);
    }

    /**
     * @dev 获取用户部署的系统
     */
    function getDeployedSystems(address user) external view returns (DeployedSystem[] memory) {
        return deployedSystems[user];
    }

    /**
     * @dev 获取用户部署的系统数量
     */
    function getDeployedSystemsCount(address user) external view returns (uint256) {
        return deployedSystems[user].length;
    }
}","keccak256":"0x7534b2eee942023fd2672a43ebe7f1b641321d2197344fb9fe1f8f6f447c7c2a","license":"MIT"},"contracts/MusicRevenueVault.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/IERC721Enumerable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title MusicRevenueVault
 * @dev 简化的音乐收益分配金库合约
 * 收集销售款项、二级市场版税、外部收入，并按比例分配给团队和持有人
 */
contract MusicRevenueVault is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    struct TeamMember {
        address account;
        uint256 basisPoints; // 基点，10000 = 100%
        string role; // "artist", "producer", "composer" etc.
    }

    struct RevenuePool {
        uint256 totalRevenue;
        uint256 teamClaimed;
        uint256 holdersClaimed;
        mapping(address => uint256) holderClaimed;
    }

    // 团队分账配置
    TeamMember[] public teamMembers;
    uint256 public teamTotalBps; // 团队总分成基点
    uint256 public holdersBps; // 持有人分成基点

    // 收益池 - 简化为单一池子（editionId = 1）
    mapping(uint256 => RevenuePool) public revenuePools;

    // NFT合约地址
    address public nftContract;

    // 支持的代币
    mapping(address => bool) public supportedTokens;

    event RevenueReceived(uint256 indexed editionId, address token, uint256 amount, string source);
    event TeamClaimed(address indexed member, uint256 indexed editionId, address token, uint256 amount);
    event HolderClaimed(address indexed holder, uint256 indexed editionId, address token, uint256 amount);
    event TeamMemberAdded(address indexed account, uint256 basisPoints, string role);

    constructor(address _nftContract) Ownable(msg.sender) {
        nftContract = _nftContract;
        supportedTokens[address(0)] = true; // ETH
    }

    /**
     * @dev 添加团队成员
     */
    function addTeamMember(address account, uint256 basisPoints, string memory role) external onlyOwner {
        require(account != address(0), "Invalid address");
        require(basisPoints > 0, "Invalid basis points");
        require(teamTotalBps + basisPoints <= 10000, "Exceeds 100%");

        teamMembers.push(TeamMember({
            account: account,
            basisPoints: basisPoints,
            role: role
        }));

        teamTotalBps += basisPoints;
        holdersBps = 10000 - teamTotalBps;

        emit TeamMemberAdded(account, basisPoints, role);
    }

    /**
     * @dev 设置支持的代币
     */
    function setSupportedToken(address token, bool supported) external onlyOwner {
        supportedTokens[token] = supported;
    }

    /**
     * @dev 接收收益（由NFT合约或预言机调用）
     */
    function receiveRevenue(uint256 editionId, address token, uint256 amount, string memory source) external payable {
        require(msg.sender == nftContract || msg.sender == owner(), "Unauthorized");
        require(supportedTokens[token], "Token not supported");

        if (token == address(0)) {
            require(msg.value == amount, "ETH amount mismatch");
        } else {
            IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        }

        revenuePools[editionId].totalRevenue += amount;

        emit RevenueReceived(editionId, token, amount, source);
    }

    /**
     * @dev 团队成员提取收益
     */
    function claimTeamRevenue(uint256 editionId, address token) external nonReentrant {
        uint256 memberIndex = _findTeamMemberIndex(msg.sender);
        require(memberIndex < teamMembers.length, "Not a team member");

        TeamMember memory member = teamMembers[memberIndex];
        RevenuePool storage pool = revenuePools[editionId];

        uint256 totalClaimable = (pool.totalRevenue * member.basisPoints) / 10000;
        uint256 alreadyClaimed = pool.holderClaimed[msg.sender];
        uint256 claimable = totalClaimable - alreadyClaimed;

        require(claimable > 0, "Nothing to claim");

        pool.holderClaimed[msg.sender] = totalClaimable;
        pool.teamClaimed += claimable;

        _transfer(token, msg.sender, claimable);

        emit TeamClaimed(msg.sender, editionId, token, claimable);
    }

    /**
     * @dev 持有人提取收益
     */
    function claimHolderRevenue(uint256 editionId, address token) external nonReentrant {
        // 获取持有数量（直接调用ERC721的balanceOf）
        uint256 holderBalance = IERC721(nftContract).balanceOf(msg.sender);
        require(holderBalance > 0, "No tokens held");

        // 获取总供应量
        uint256 totalSupply = IERC721Enumerable(nftContract).totalSupply();
        require(totalSupply > 0, "No tokens minted");

        RevenuePool storage pool = revenuePools[editionId];

        // 计算持有人应得的总收益
        uint256 holdersTotal = (pool.totalRevenue * holdersBps) / 10000;
        uint256 holderShare = (holdersTotal * holderBalance) / totalSupply;
        uint256 alreadyClaimed = pool.holderClaimed[msg.sender];
        uint256 claimable = holderShare - alreadyClaimed;

        require(claimable > 0, "Nothing to claim");

        pool.holderClaimed[msg.sender] = holderShare;
        pool.holdersClaimed += claimable;

        _transfer(token, msg.sender, claimable);

        emit HolderClaimed(msg.sender, editionId, token, claimable);
    }

    /**
     * @dev 查看可提取的收益
     */
    function getClaimableRevenue(address account, uint256 editionId, address token) 
        external view returns (uint256) {
        RevenuePool storage pool = revenuePools[editionId];

        // 检查是否为团队成员
        uint256 memberIndex = _findTeamMemberIndex(account);
        if (memberIndex < teamMembers.length) {
            TeamMember memory member = teamMembers[memberIndex];
            uint256 totalClaimable = (pool.totalRevenue * member.basisPoints) / 10000;
            return totalClaimable - pool.holderClaimed[account];
        }

        // 持有人收益计算
        uint256 holderBalance = IERC721(nftContract).balanceOf(account);
        if (holderBalance == 0) return 0;

        uint256 totalSupply = IERC721Enumerable(nftContract).totalSupply();
        if (totalSupply == 0) return 0;

        uint256 holdersTotal = (pool.totalRevenue * holdersBps) / 10000;
        uint256 holderShare = (holdersTotal * holderBalance) / totalSupply;

        return holderShare - pool.holderClaimed[account];
    }

    /**
     * @dev 内部函数：查找团队成员索引
     */
    function _findTeamMemberIndex(address account) internal view returns (uint256) {
        for (uint256 i = 0; i < teamMembers.length; i++) {
            if (teamMembers[i].account == account) {
                return i;
            }
        }
        return type(uint256).max; // 未找到
    }

    /**
     * @dev 内部函数：转账
     */
    function _transfer(address token, address to, uint256 amount) internal {
        if (token == address(0)) {
            payable(to).transfer(amount);
        } else {
            IERC20(token).safeTransfer(to, amount);
        }
    }

    /**
     * @dev 获取团队成员信息
     */
    function getTeamMembers() external view returns (TeamMember[] memory) {
        return teamMembers;
    }

    /**
     * @dev 获取收益池信息
     */
    function getRevenuePool(uint256 editionId) external view returns (
        uint256 totalRevenue,
        uint256 teamClaimed,
        uint256 holdersClaimed
    ) {
        RevenuePool storage pool = revenuePools[editionId];
        return (pool.totalRevenue, pool.teamClaimed, pool.holdersClaimed);
    }

    // 接收ETH
    receive() external payable {}
